function [MIDI,note]=freq2note(freq)% Umsetzung Frequenzwert in MIDI-Wert und Notename% https://www.zem-college.de/midi/mc_taben.htm% Frequenzen werden in der temperierten Stimmung abgerundetMIDI=round(69+12*log2(freq/440));oktave=9-floor((131-MIDI)/12);note_in_oktave=MIDI-12*(oktave+1);switch note_in_oktave	case 0, name='c';	case 1, name='cis';	case 2, name='d';	case 3, name='dis';	case 4, name='e';	case 5, name='f';	case 6, name='fis';	case 7, name='g';	case 8, name='gis';	case 9, name='a';	case 10, name='b';	case 11, name='h';endnote=[name,' ',num2str(oktave)];